INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2153,2283,104401,1,1,1,0,1,'世界の前に、\nこの部屋を征服\nしてやろう♪',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2154,2284,104401,1,2,1,0,1,'拠点とはやはり\n斯くあるべきじゃの',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2155,2285,104401,1,3,1,0,1,'眷属の物はわらわの物\n故に、この部屋は\nわらわの部屋じゃな！',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2156,2286,104401,1,4,1,0,1,'永く眠っている間に\n随分と世界は変わった\nようじゃな…',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2157,2287,104401,1,5,1,0,1,'うるさいのう～\nまったく、近頃の\n若いもんは…',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2158,2288,104401,1,101,4,0,1,'あやつが来てから\n動悸が治まらぬ…\n如何ような病で\nあろうかの…',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2159,2289,104401,1,102,8,0,1,'眷属よ、どこに行った\nのじゃ？\nわらわの世話を\n許可するのじゃ',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2160,2290,104401,2,1,1,0,1,'いずれ世界は\n我が手中に収まる\nふふふ、\n楽しみじゃのう',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2161,2291,104401,2,2,1,0,1,'眷属よ、わらわから\n離れてはならんぞ！\n子供に戻ってしまう\nではないか！',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2162,2292,104401,2,3,1,0,1,'トマトジュースが\n欲しいのじゃ！\n野菜ジュースでは\nダメだぞ！',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2163,2293,104401,2,4,1,0,1,'蚊にくわれたぞ！\nわらわの血を吸う\nとは～！',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2164,2294,104401,2,101,4,0,1,'こら、頭をなでるな\nお主はこの姿でも\n子供扱いするのか？',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2165,2295,104401,2,102,8,0,1,'お主が望むなら世界の\n半分をくれてやっても\n良いのじゃぞ？',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2166,2296,104401,2,1,1,1,1,'朝早くから感心じゃの\nさすがわらわの\n眷属じゃ',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2167,2297,104401,2,2,1,2,1,'太陽は苦手じゃ…\nこの部屋の中で\n過ごそうぞ',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2168,2298,104401,2,3,1,3,1,'夜も更けてきた！\nわらわ達の時間は\nこれからじゃ～♪',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2169,2300,104401,3,2,1,0,1,'よい座り心地じゃ\nわらわの玉座としよう',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2170,2301,104401,3,3,1,0,1,'可愛いではないか\nわらわの眷属に\nしてやろうかのう',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2171,2302,104401,3,4,1,0,1,'うむ、良い気持ちじゃ\nこれを作った者、\n褒めてつかわすぞ',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2172,2303,104401,3,5,1,0,1,'あたたかい…\n身も心も\n浄化されるようじゃ',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2173,2305,104401,3,7,1,0,1,'吸血鬼のわらわが、\nこのようなもの…\nうまい♪',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2174,2306,104401,3,8,1,0,1,'このまま大人の姿を\n保つ方法…どこかに\n書いてないかのう',0);
