UPDATE unit_comments SET description='ゴーヤーにソーキ…\nサーターアンダギー！\n故郷の食べ物が恋しいさ～' WHERE id=1017003;
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1044001,104401,1,1,6,5.0,1,'お主…わらわと何処かで\n会ったことはないか？\nお主を見ていると、何故か\nなつかしさを覚えるのじゃが…');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1044002,104401,1,2,4,6.4,1,'むう…どういうわけか\n無性にトマトジュースが\n飲みたい気分じゃの…\nお主、どこぞで求めてまいれ');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1044003,104401,1,3,4,6.9,2,'昼間は思うように動けぬ…\n夜になるまで、じっとしておこう\nそれまでお主は、わらわの\n話し相手になるのじゃぞ');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1044004,104401,1,4,6,4.2,4,'お主はちっとも、わらわを\n恐れないのじゃな？\nな？　可愛らしいじゃと？\n眷属の分際で何を申すー！');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1044005,104401,1,5,2,9.0,5,'お主は、特別な眷属じゃ\n今より「昼を総べる者」を\n名乗るが良いぞ');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1044006,104431,1,1,1,0.0,1,'わらわの真の姿は美しかろう？\nなに？　見慣れてないから\nいつもの方が良い？　なんと\n張り合いのないことを…！');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1044007,104431,1,2,1,0.0,1,'わらわの野望を聞きたいのか？\nもちろん世界征服じゃ！\n女と生まれたからには\n一度は目指したいものよの♪');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1044008,104431,1,3,1,0.0,1,'わらわは伝説の吸血鬼。\nお主の命を奪うなど\nたやすいことじゃが…まあ、\n眷属ゆえ生かしておいてやろうぞ');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1044009,104431,1,4,1,0.0,1,'世界を征服した暁には\nその半分をお主に分け与えよう\nど、どういう意味かわかるな？');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1044010,104431,1,5,1,0.0,1,'昼間にお主と会うために、\n最近は昼夜逆転してしまった…\n昼ふかしは、美容によくないの\nじゃが…');
UPDATE unit_data SET rarity=3, motion_type=3, se_type=3, move_speed=450, search_area_width=425, atk_type=2, normal_atk_cast_time=1.425, cutin_1=104401, guild_id=12, comment='【魔法】中衛で命を削り攻撃する「夜を統べる者」。\n自身のＨＰと引き換えに放つスキルは高い殲滅力を誇り\n範囲攻撃における威力では他の追随を許さない。' WHERE unit_id=104401;
