INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2200,2333,107501,1,1,1,0,1,'またお魚を\n捕りにいこうかな♪',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2201,2334,107501,1,2,1,0,1,'そろそろみんなも\nお腹が空くころ\nじゃないですか～？',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2202,2335,107501,1,3,1,0,1,'ここなら、\n日焼けの心配は\nありませんね！',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2203,2336,107501,1,4,1,0,1,'お刺身に焼き貝…\nお腹が魚介類を\n求めてますっ！',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2204,2337,107501,1,5,1,0,1,'泳ぐのも食べるのも\n大得意です！',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2205,2338,107501,1,101,4,0,1,'あの人と二人で\n浜辺を散歩…\n素敵ですねぇ',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2206,2339,107501,1,102,8,0,1,'あの人に水着を\n褒めてもらえたら、\nすごく嬉しいなぁ',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2207,2340,107501,2,1,1,0,1,'たまには釣りじゃなく\n一緒に素潜りでも\n行きませんか？',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2208,2341,107501,2,2,1,0,1,'男の子に水着を\n見られるのって\n少し緊張しちゃいます',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2209,2342,107501,2,3,1,0,1,'ギルドハウスの\nみんなとも、\n海に行きたいですね！',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2210,2343,107501,2,4,1,0,1,'あなたの水着、\n私が選んであげます！',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2211,2344,107501,2,101,4,0,1,'泳ぎの練習なら\n付き合いますよ！\n手取り足取り♪',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2212,2345,107501,2,102,8,0,1,'水着姿が好きなら\nいくらでも\n着てあげますよぉ♪',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2213,2346,107501,2,1,1,1,1,'朝の海も\n幻想的で\nいいですよねぇ～！',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2214,2347,107501,2,2,1,2,1,'お昼はみんなで、\n海鮮バーベキューとか\nどうでしょう！',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2215,2348,107501,2,3,1,3,1,'夜の海は星が\n綺麗ですよ！\n見に行きましょう！',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2216,2350,107501,3,2,1,0,1,'泳ぎ疲れたので\nちょっと休憩です～',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2217,2351,107501,3,3,1,0,1,'あなたにも、\n可愛い水着を\n着せてあげたいです',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2218,2352,107501,3,4,1,0,1,'なんだか、\nリゾート気分です…',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2219,2353,107501,3,5,1,0,1,'ふい～、泳ぎで冷えた\n身体を暖めましょう～',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2220,2355,107501,3,7,1,0,1,'おいひ～です～\nこれ、海水浴にも\n持っていきたいですね',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2221,2356,107501,3,8,1,0,1,'南国のフルーツ図鑑\n…じゅるり…',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2222,2358,107601,1,1,1,0,1,'この格好で\n部屋をうろつくのは\n不思議な感じがします',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2223,2359,107601,1,2,1,0,1,'南国の果実を使って\n主さまにデザートを\nお作りしましょう',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2224,2360,107601,1,3,1,0,1,'せっかくの水着ですし\nこれから定期的に\n海水浴に行きたいです',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2225,2361,107601,1,4,1,0,1,'海の生き物も、\n森の生き物に負けず\n可愛かったです…',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2226,2362,107601,1,5,1,0,1,'クロールの練習なら、\n部屋でもできそうです',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2227,2363,107601,1,101,4,0,1,'主さまに、水着姿を\n褒めていただけた…\nえへへ♪',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2228,2364,107601,1,102,8,0,1,'水着で主さまの\nお世話をしたら、\n喜びますでしょうか…',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2229,2365,107601,2,1,1,0,1,'お疲れでしたら\nわたくしの膝を\nお使いくださいね',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2230,2366,107601,2,2,1,0,1,'主さま、海の家で\nつかうおこづかいを\n渡しておきますね',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2231,2367,107601,2,3,1,0,1,'海の美しさは、\n森とはちがう\n魅力がありました',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2232,2368,107601,2,4,1,0,1,'この髪飾りですか？\n造花ですから\n水の中も平気です',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2233,2369,107601,2,101,4,0,1,'水着でくっついて\n眠ると、いつもより\n密着して暖かいです',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2234,2370,107601,2,102,8,0,1,'はじめての海が、\n主さまと一緒で\n幸せです♪',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2235,2371,107601,2,1,1,1,1,'おはようございます。\n泳ぎに行く準備は\n万端です',0);
INSERT INTO room_unit_comments(rowid,id,unit_id,"trigger",voice_id,beloved_step,time,face_id,description,insert_word_type) VALUES(2236,2372,107601,2,2,1,2,1,'午後は泳ぐのをやめて\nスイカ割りなど\nいかがでしょう？',0);
