INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1075001,107501,1,1,1,2.2,2,'水着買っちゃいました～！\nどうですどうです？\n似合ってますか～？');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1075002,107501,1,2,1,0.0,1,'この水着、結構動きやすいので\n素潜りとかして\n海の幸をゲットできそうです！');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1075003,107501,1,3,2,0.0,2,'マリンスポーツしましょう！\nビーチボールとか、\nビーチフラッグとか！\n負けませんよ～');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1075004,107501,1,4,5,4.3,2,'私の水着姿で、\nあなたを誘惑しちゃいます♪\nなんちゃってー！');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1075005,107501,1,5,1,5.0,2,'せっかくの水着ですし、\nたくさん泳いで、\nたくさんはしゃいで…\n思い出いっぱい作りましょうね！');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1075006,107531,1,1,1,0.0,1,'わぁっ、やりましたね～！\n塩水しょっぱいぃ～\nこうなったらわたしも本気です！\n覚悟してくださいよぉ～！');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1075007,107531,1,2,1,0.0,1,'青い海、白い砂浜、輝く太陽！\nそしておいしいお魚！\nここは天国ですね！');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1075008,107531,1,3,1,0.0,1,'水が気持ちいいですね～\nこんなに充実した夏、\n生まれて初めてです！');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1075009,107531,1,4,1,0.0,1,'えへへ♪\nあなたと海で遊ぶなんて、\nとっても新鮮な感じです！\n青春ですね♪');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1075010,107531,1,5,1,0.0,1,'はしゃぎすぎて水着が\n流されちゃったりしたら、\nあなたに抱きついて\n隠しますね！');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1076001,107601,1,1,1,4.8,5,'救護院の皆さまに、水着を\n選んでいただきました…\n似合っていますでしょうか？');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1076002,107601,1,2,1,0.0,1,'主さま、よろしければ\n一緒に泳ぎの\n練習をしましょう');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1076003,107601,1,3,2,0.0,2,'主さま。わたくし、\n砂のお城というものを\n作ってみたいのですが、\nお手伝いいただけますでしょうか');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1076004,107601,1,4,5,0.0,5,'このような\n過度な肌の露出は、\nやはり恥ずかしいです…\n主さまにしかお見せできません');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1076005,107601,1,5,1,4.2,2,'日焼け止めを塗っていただいても\nよいでしょうか？\n主さまにでしたら、\n肌を許すことができます');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1076006,107631,1,1,1,0.0,1,'主さま。どんな場所でも、\nわたくしがついております\nこの手を離さないでくださいね');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1076007,107631,1,2,1,0.0,1,'見てください、主さま\nおさかながこんなに近くに…\nとても神秘的でございます');
