UPDATE unit_comments SET description='わらわは伝説の吸血鬼\nお主の命を奪うなど\nたやすいことじゃが…まあ、\n眷属ゆえ生かしておいてやろうぞ' WHERE id=1044008;
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1054001,105401,1,1,1,1.9,5,'いいですか騎士さん\nレイ様に変なことしたら\nただじゃおきませんよ！');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1054002,105401,1,2,1,2.8,2,'騎士さんがレイ様に\n変なことしないように\n私がしっかり見張ります！');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1054003,105401,1,3,4,2.0,1,'ああ、また服が…\nちょっとこっちに来てください\nすぐに直してあげますから');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1054004,105401,1,4,4,4.8,6,'あの…騎士さんって\nやっぱりレイ様のこと…\nな、なんでもないです！\n忘れてください！');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1054005,105401,1,5,5,6.5,6,'どうして騎士さんは\nひどいことした私にここまで\n優しくしてくれるんですか？\nまさか…また縛られたいとか！？');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1054006,105431,1,1,1,0.0,1,'何匹来ても平気です！\n騎士さんがそばにいてくれるから！\n…変な意味じゃありませんよ！？');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1054007,105431,1,2,1,0.0,1,'そこはさすがに近すぎます\nもう少し下がってください\nさもないと、日頃の鬱憤\nぶつけちゃいますよ？');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1054008,105431,1,3,1,0.0,1,'素材が余ったら騎士さんにも\n何か作ってあげましょうか\nそうですね…何がいいですか？');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1054009,105431,1,4,1,0.0,1,'最近も…相変わらずレイ様と\n仲いいんですか…？\nき、聞いてみただけですっ！');
INSERT INTO unit_comments(id,unit_id,use_type,voice_id,face_id,change_time,change_face,description) VALUES(1054010,105431,1,5,1,0.0,1,'騎士さんは私に何か\n手伝ってほしいことあります？\n報酬は特別になしでいいです');
